import React, { useState } from 'react'
import { Header } from './Header'
import Button from 'react-bootstrap/Button';
import Form from 'react-bootstrap/Form';


export const AddProduct = () => {

  const [name, setName] = useState("");
  const [model_no, setModel] = useState("");
  const [image, setImage] = useState("");
  const [description, setDescription] = useState("");
  const [price, setPrice] = useState("");


  async function addproduct() {

    let item = { name, model, image, description, price };

    const formData = new FormData();
    formData.append("image", image);
    formData.append("model_no", model_no);
    formData.append("name", name);
    formData.append("description", description);
    formData.append("price", price);


    let result = await fetch("http://127.0.0.1:8000/api/addproduct", {
              method:"POST",
             body:formData
    });
    alert("Data has been saved");


  }


  return (
    <div>
      <Header />

      <div className="col-md-6 offset-sm-3 border shadow mt-20">
        <h1>Add Products</h1>
        <Form>

          <Form.Group className="m-5" controlId="">
            <Form.Control type="text" placeholder="Product Name" value={name} onChange={(e) => setName(e.target.value)} />
          </Form.Group>

          <Form.Group className="m-5" controlId="formBasicLastname">
            <Form.Control type="text" placeholder="Model Number" value={model_no} onChange={(e) => setModel(e.target.value)} />
          </Form.Group>

          <Form.Group className="m-5" controlId="formBasicEmail">
            <small className='-m10'> Select Product Image *</small>
            <Form.Control type="file" value={image} onChange={(e) => setImage(e.target.value)} />
          </Form.Group>

          <Form.Group className="m-5" controlId="formBasicLastname">
            <Form.Control type="text" placeholder="â‚¹ Price " value={price} onChange={(e) => setPrice(e.target.value)} />
          </Form.Group>

          <textarea rows={3} placeholder='Product Description' value={description} onChange={(e) => setDescription(e.target.value)}></textarea><br />

          <Button variant="primary mb-3" onClick={addproduct}>
            Submit
          </Button>
        </Form>
      </div>
    </div>
  )
}
