import React, { useEffect, useState } from 'react'
import { Header } from './Header'
import Button from 'react-bootstrap/Button';
import Form from 'react-bootstrap/Form';
import { useParams } from 'react-router-dom';

export const UpdateProduct = (props) => {
  const [data, setData] = useState();
  const param = useParams();
  const url = "http://127.0.0.1:8000/api/getdata/" + param.id;

  function getInfo() {
    return fetch(url)
      .then((res) => res.json())
      .then((d) => setData(d))

  }

  useEffect(() => {
    getInfo();
  }, [])

  console.log("getinfo", getInfo());

  return (
    <div>
      <Header />
      <header>
        <h1>Update Products</h1>
      </header>
      <div className="col-md-6 offset-sm-3 border shadow mt-20">

        {
          data.map((i, index) =>
            <Form>

              <Form.Group className="m-5" controlId="">
                <Form.Control type="text" def placeholder="Product Name" />
              </Form.Group>

              <Form.Group className="m-5" controlId="formBasicLastname">
                <Form.Control type="text" placeholder="Model Number" />
              </Form.Group>

              <Form.Group className="m-5" controlId="formBasicEmail">
                <small className='-m10'> Select Product Image *</small>
                <Form.Control type="file" />
              </Form.Group>

              <Form.Group className="m-5" controlId="formBasicLastname">
                <Form.Control type="text" placeholder="â‚¹ Price " />
              </Form.Group>

              <textarea rows={3} placeholder='Product Description' ></textarea><br />

              <Button variant="primary mb-3" >
                Submit
              </Button>
            </Form>
          )
        }

      </div>
    </div>
  )
}
